import React from "react";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { Storys } from "../data";
import "../app.css";

const Container = styled.div`
  display: flex;
`;
const Wrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
  overflow: hidden;
  margin: 30px;
`;
const Card = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: column;
  flex: 1;
  margin: 20px;
  padding: 20px;
  border-radius: 10px;
  background-color: ${(props) => props.bg};
  -webkit-box-shadow: 0px 0px 15px -10px rgba(0, 0, 0, 0.75);
  box-shadow: 0px 0px 15px -10px rgba(20, 0, 0.75);
`;

const Cardcon = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 5px;
`;

const Title = styled.h3`
  text-align: center;
`;
const Img = styled.img`
  height: 250px;
  width: 250px;
  object-fit: cover;
  border-radius: 10px;
`;
const Desc = styled.p`
  text-align: center;
`;
const Button = styled.button`
  padding: 5px 25px;
  font-size: 20px;
  border-radius: 10px;
  outline: none;
  border: none;
  text-decoration: none;
  background-color: #f7cf9a;
`;

const Activities = () => {
  const [cards, setCards] = useState([]);

  const getCards = async () => {
    const res = await fetch("https://api.pokemontcg.io/v2/cards");
    setCards(await res.json());
  };

  useEffect(() => {
    getCards();
  }, []);

  return (
    <div>
      <Container>
        <Wrapper>
          {cards.map((item) => (
            <Link className={"list"} key={item.id}>
              <Card bg={item.bg}>
                <Cardcon>
                  <Title>{item.title}</Title>
                </Cardcon>
                <Cardcon>
                  <Img src={item.img} />
                </Cardcon>
                <Cardcon>
                  <Desc>{item.desc}</Desc>
                </Cardcon>

                <Cardcon>
                  <Button>Read</Button>
                </Cardcon>
              </Card>
            </Link>
          ))}
        </Wrapper>
      </Container>
      <Footer />
    </div>
  );
};

export default Activities;
